    def __init__(self):
        HTMLParser.__init__(self)
        self.links = []

    '''
    handle_starttag:处理开始标签,例如<body>
        tag 表示解析到的任何标签
        attrs是一个序列, 序列中的内容为元组,
        一个元组中有两个元素, 分别是该标签的属性和该属性的值判断逻辑语句
    '''

    def handle_starttag(self, tag, attrs):
        # print('<%s>' % tag, 'attrs:', attrs)
        if tag == 'a':
            if len(attrs) == 0:
                pass
            else:
                for (variable, value) in attrs:
                    if variable == 'href':
                        self.links.append(value)

    '''
    handle_endtag:处理结束标签
        这里tag对应的上面那个标签的结束标签 
        例如上的标签为<p>，则这里对应的是</p> 
    '''

    def handle_endtag(self, tag):
        # print('</%s>' % tag)
        pass

    '''
    handle_startendtag:处理开始标签和结束标签,例如:<img src='http://www.baidu.com/logo.gif'/>
    '''

    def handle_startendtag(self, tag, attrs):
        # print('<%s/>' % tag, 'attrs:', attrs)
        pass

    '''
    handle_data:处理数据,就是<xx>data</xx>中间的那些数据 
    '''

    def handle_data(self, data):
        print(data)
        # pass

    '''
    handle_comment:处理注释
    '''

    def handle_comment(self, data):
        # print('<!--', data, '-->')
        pass

    '''
    handle_entityref:处理一些特殊符号,以&开头的
    '''

    def handle_entityref(self, name):
        # print('&%s;' % name)
        pass

    '''
    handle_charref:处理特殊字符串,就是以&#开头的,一般是内码表示的字符
    '''

